    override func viewDidAppear(_ animated: Bool) {
        super.viewDidAppear(animated)
        let size = textView.sizeThatFits(self.textView.bounds.size)
        if size.height > UIScreen.main.bounds.size.height {
            self.view.layoutIfNeeded()
            let animateAmount = size.height - UIScreen.main.bounds.size.height
            UIView.animate(withDuration: 2, animations: {
                self.topConstraint.constant -= animateAmount
                self.view.layoutIfNeeded()
            })
        }
    }
